import { Input } from "./types";

function convert_json_to_input(raw: {
  job_id: number;
  title: string;
  location: string | null;
  department: string | null;
  salary_range: string | null;
  company_profile: string | null;
  description: string | null;
  requirements: string | null;
  benefits: string | null;
  telecommuting: number;
  has_company_logo: number;
  has_questions: number;
  employment_type: string | null;
  required_experience: string | null;
  required_education: string | null;
  industry: string | null;
  function: string | null;
  fraudulent: number | null;
}): Input {
  const {
    title,
    location,
    department,
    salary_range,
    company_profile,
    description,
    requirements,
    benefits,
    telecommuting,
    has_company_logo,
    has_questions,
    employment_type,
    required_experience,
    required_education,
    industry,
    // function, // Reserved keyword?
  } = raw;
  return {
    title,
    location: location ?? "-",
    department: department ?? "-",
    salary_lower: Number((salary_range || "0-0").split("-").at(0)),
    salary_upper: Number((salary_range || "0-0").split("-").at(1)),
    company_profile: company_profile ?? "-",
    description: description ?? "-",
    requirements: requirements ?? "-",
    benefits: benefits ?? "-",
    telecommuting: telecommuting ? true : false,
    has_company_logo: has_company_logo ? true : false,
    has_questions: has_questions ? true : false,
    employment_type: employment_type ?? "-",
    required_experience: required_experience ?? "-",
    required_education: required_education ?? "-",
    industry: industry ?? "-",
    function: raw.function ?? "-",
  };
}

